# adding the list of directors and the list of actors to the database
for direct in d:
    Director.objects.create(name=direct)

for act in a:
    Actor.objects.create(name=act)

for gen in g:
    Genre.objects.create(name=gen)


# this includes all the data on the movies
movies_path = "movies.json"


with open(movies_path) as f:
    data = json.load(f)

# looping through each movie da
for movie in data:
    x = Movie.objects.create(title=movie['title'], runtime=movie['runtime'],
                             description=movie['description'], rating=movie['rating'], year=movie['year'],
                             certificate=movie['certificate'], icon_url=movie['icon_url'],
                             big_poster_url=movie['big_poster_url'],
                             poster_url=movie['poster_url'])

    # loop through the actors in the movie
    for actor in movie['actors']:
        actor = Actor.objects.filter(name__exact=actor)[0]
        x.actors.add(actor)

    for director in movie['directors']:
        director = Director.objects.filter(name__exact=director)[0]
        x.directors.add(director)

    # we loop through the genres in the movie
    for gen in movie['genre']:
        # the variable genre then equals the corresponding Genre object
        gen = Genre.objects.filter(name__exact=gen)[0]
        # we then attach that object to the movie
        x.genre.add(gen)
